import React, { Component,useState } from "react";
import ReactDOM from "react-dom";
import ReactPlayer from "react-player";
import { createPortal } from "react-dom";
import "./style.css";

function App() {
  const [seconds, setSeconds, showDialog] = useState(0);
  const handleProgress = (secs) => {
    setSeconds(secs);
    showDialog = true;
  };
  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      {console.log(seconds)}
      <button style={parseFloat(seconds) < 10 ? { display: "none" } : {}}>
        more than 10
      </button>
      <h2>ReactPlayer</h2>
      <div style={{ height: "100vh" }}>
        <ReactPlayer
          url="https://vimeo.com/226260195"
          className="react-player"
          playing={false}
          width="100%"
          height="100%"
          onProgress={(e) => handleProgress(e.playedSeconds)}
        />
         {/* Show Modal - Renders Outside React Hierarchy Tree via Portal Pattern */}
         {showDialog === true ? (
                    <DialogModal>
                        <div className="dialog-wrapper">
                            <h1>New List Item</h1>
                            <form onSubmit={this._onSubmit}>
                                <input type="text" value={seconds} onChange={this._onChange} />
                            </form>
                        </div>
                    </DialogModal>
                ) : null}
      </div>
    </div>
  );
}

class DialogModal extends Component {
  constructor() {
      super();
      this.body = document.getElementsByTagName("body")[0];
      this.el = document.createElement("div");
      this.el.id = "dialog-root";
  }

  componentDidMount() {
      this.body.appendChild(this.el);
  }

  componentWillUnmount() {
      this.body.removeChild(this.el);
  }

  render() {
      return createPortal(this.props.children, this.el);
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
