import React, { Component,useState } from "react";
import ReactPlayer from "react-player";
import { createPortal } from "react-dom";
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import "./index.css";
import "./style.css";
import Button from '@mui/material/Button';
import PropTypes from 'prop-types';
import Avatar from '@mui/material/Avatar';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import PersonIcon from '@mui/icons-material/Person';
import AddIcon from '@mui/icons-material/Add';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';

class App extends Component {
  state = {
    url: 'https://www.youtube.com/watch?v=oUFJJNQGwhk',
    open: false,
    pip: false,
    playing: true,
    controls: true,
    light: false,
    volume: 0.8,
    muted: false,
    showDialog: false,
    played: 0,
    input: "",
    loaded: 0,
    duration: 0,
    playbackRate: 1.0,
    loop: false
  }

  load = url => {
    this.setState({
      url,
      played: 0,
      loaded: 0,
      pip: false
    })
  }

  //const [open, setOpen] = React.useState(false);

  handleClickOpen = () => {
    //setOpen(true);
  };

  handleClose = () => {
    //setOpen(false);
  };

  handlePlayPause = () => {
    this.setState({ playing: !this.state.playing })
  }

  handleStop = () => {
    this.setState({ url: null, playing: false })
  }

  handleToggleControls = () => {
    const url = this.state.url
    this.setState({
      controls: !this.state.controls,
      url: null
    }, () => this.load(url))
  }

  handleSetPlaybackRate = e => {
    this.setState({ playbackRate: parseFloat(e.target.value) })
  }

  handleOnPlaybackRateChange = (speed) => {
    this.setState({ playbackRate: parseFloat(speed) })
  }


  handlePlay = () => {
    console.log('onPlay')
    this.setState({ playing: true })
  }

  handlePause = () => {
    console.log('onPause')
    this.setState({ playing: false })
  }

  handleSeekMouseDown = e => {
    this.setState({ seeking: true })
  }

  handleSeekChange = e => {
    this.setState({ played: parseFloat(e.target.value) })
  }

  handleSeekMouseUp = e => {
    this.setState({ seeking: false })
    this.player.seekTo(parseFloat(e.target.value))
  }

  handleProgress = state => {
    console.log('onProgress', state);
    this.setState({ loaded: state.playedSeconds })
    if(state.playedSeconds > 10)
    {
      this.setState({ open: true })
    }
    //this.setState({ loaded : 20 }); 
    // We only want to update time slider if we are not currently seeking
    // if (!this.state.seeking) {
    //   this.setState(state)
    // }
  }

  handleEnded = () => {
    console.log('onEnded')
    this.setState({ playing: this.state.loop })
  }

  handleDuration = (duration) => {
    console.log('onDuration', duration)
    this.setState({ duration })
  }

  renderLoadButton = (url, label) => {
    return (
      <button onClick={() => this.load(url)}>
        {label}
      </button>
    )
  }

  _onChange(e) {
    let input = e.target.value;

    this.setState({ input });
}

  ref = player => {
    this.player = player
  }

  render () {
    const { url, playing, controls,open, light, volume, muted, loop, played, input,showDialog, loaded, duration, playbackRate, pip } = this.state
    const SEPARATOR = ' Â· '

    return (
      <div className="container">
       <div className='app'>

            <ReactPlayer
              ref={this.ref}
              className='react-player'
              width='100%'
              height='100%'
              url={url}
              playing={playing}
              controls={controls}
              // onReady={() => console.log('onReady')}
              // onStart={() => console.log('onStart')}
              onPlay={this.handlePlay}
              onPause={this.handlePause}
              // onBuffer={() => console.log('onBuffer')}
              // onPlaybackRateChange={this.handleOnPlaybackRateChange}
              // onSeek={e => console.log('onSeek', e)}
              // onEnded={this.handleEnded}
              // onError={e => console.log('onError', e)}
              onProgress={this.handleProgress}
              // onDuration={this.handleDuration}
            />

          {/* {showDialog === true ? (
                    <DialogModal>
                        <div className="dialog-wrapper">
                            <h1>New List Item</h1>
                            <form onSubmit={this._onSubmit}>
                                <input type="text" value={input} onChange={this._onChange} />
                            </form>
                        </div>
                    </DialogModal>
                ) : null} */}

      <Dialog open={open} onClose={this.handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
           3+2
          </DialogContentText>
          <List sx={{ pt: 0 }}>
        {emails.map((email) => (
          <ListItem button onClick={() => handleListItemClick(email)} key={email}>
            <ListItemAvatar>
              <Avatar sx={{ bgcolor: blue[100], color: blue[600] }}>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary={email} />
          </ListItem>
        ))}

        <ListItem autoFocus button onClick={() => handleListItemClick('addAccount')}>
          <ListItemAvatar>
            <Avatar>
              <AddIcon />
            </Avatar>
          </ListItemAvatar>
          <ListItemText primary="Add account" />
        </ListItem>
      </List>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleClose}>Cancel</Button>
          <Button onClick={this.handleClose}>Subscribe</Button>
        </DialogActions>
      </Dialog>

          </div>
      </div>
    )
  }
}

class DialogModal extends Component {
  constructor() {
      super();
      this.body = document.getElementsByTagName("body")[0];
      this.el = document.createElement("div");
      this.el.id = "dialog-root";
  }

  componentDidMount() {
      this.body.appendChild(this.el);
  }

  componentWillUnmount() {
      this.body.removeChild(this.el);
  }

  render() {
      return createPortal(this.props.children, this.el);
  }
}

export default (App)
